import React, { Component } from "react";
import {validateAddUser} from '../../utils/validate';

class Modal extends Component {
  constructor(props) {
    super(props)
    this.state = {
      user:{
        username: '',
        name: '',
        email: '',
        phone: '',
        type: '',
      },
      error:{
        username: '',
        name: '',
        email: '',
        phone: '',
        type: '',
      },
    };
  }

  render() {
    return (
      <div
        className="modal fade"
        id="modelId"
        tabIndex={-1}
        role="dialog"
        aria-labelledby="modelTitleId"
        aria-hidden="true"
      >
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">ADD USER</h5>
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">Ã—</span>
              </button>
            </div>
            <div className="modal-body">
              <form onSubmit={this.handleSubmit}>
                <div className="form-group">
                  <label>Username</label>
                  <input
                    type="text"
                    className="form-control"
                    onChange={this.handleChange}
                    name="username"
                  />
                  {
                    this.state.error.username && (
                      <p className="alert alert-danger">{this.state.error.username}</p>
                      )
                  }
                </div>
                <div className="form-group">
                  <label>Name</label>
                  <input
                    type="text"
                    className="form-control"
                    onChange={this.handleChange}
                    name="name"
                  />
                  {
                    this.state.error.name && (
                      <p className="alert alert-danger">{this.state.error.name}</p>
                      )
                  }
                </div>
                <div className="form-group">
                  <label>Email</label>
                  <input
                    type="text"
                    className="form-control"
                    onChange={this.handleChange}
                    name="email"
                  />
                  {
                    this.state.error.email && (
                      <p className="alert alert-danger">{this.state.error.email}</p>
                      )
                  }
                </div>
                <div className="form-group">
                  <label>Phone Number</label>
                  <input
                    type="text"
                    className="form-control"
                    onChange={this.handleChange}
                    name="phone"
                  />
                  {
                    this.state.error.phone && (
                      <p className="alert alert-danger">{this.state.error.phone}</p>
                      )
                  }
                </div>
                <div className="form-group">
                  <label>Type</label>
                  <select type="text" className="form-control"
                  onChange={this.handleChange}
                  name="type">
                    <option>Choose One</option>
                    <option>Admin</option>
                    <option>User</option>
                  </select>
                  {
                    this.state.error.type && (
                      <p className="alert alert-danger">{this.state.error.type}</p>
                      )
                  }
                </div>
                <button type="submit" className="btn btn-success">
                  Submit
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
  handleChange = (event)=>{
    const errorMessage= validateAddUser(event.target.name, event.target.value);
    this.setState({
      user:{...this.state.user,[event.target.name]: event.target.value},
      error:{
        ...this.state.error,[event.target.name]:errorMessage}
    },)
  }
  handleSubmit = (event)=>{
    event.preventDefault();
    console.log(this.state);
  }
  validateAll
}

export default Modal;
